                    matrix[i][j], matrix[j][i] = matrix[j][i], matrix[i][j]

            #水平方向翻转

            for i in range(h):
                start = 0
                end = h-1
                while start<end:
                    matrix[i][start], matrix[i][end] = matrix[i][end], matrix[i][start]
                    start+=1
                    end-=1

        def compare(mat, target):
