class Solution(object):
    def subdomainVisits(self, cpdomains):
        """
        :type cpdomains: List[str]
        :rtype: List[str]
        """
        res = {}
        for x in cpdomains:
            times, domain = x.split(' ')
            subdomain = domain.split('.')
            for x in range(len(subdomain)):
                d = '.'.join(subdomain[x:])
