class Solution(object):
    def findBottomLeftValue(self, root):
        """
        :type root: TreeNode
        :rtype: int
        """
        info = []
        def dfs(node, depth=0):
            if node is None:
                return
            if len(info) <= depth:
                info.append(None)
            if info[depth] is None:
                info[depth] = node.val
            dfs(node.left, depth + 1)
